/* eslint-disable jsx-a11y/heading-has-content */
import React from 'react';
import Spinner from './Spinner';


const Card = ({loadingData, showData, weather, forecast}) => {

    var today = new Date();
    var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
    var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    var dateTime = date+' '+time;

    var url = "";

    if(loadingData){

        return  <Spinner/>;
    }
    if (showData){
        url = "https://openweathermap.org/img/w/" + weather.weather[0].icon + ".png";
    }

    if(showData){
        return (
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{weather.name}</h5>
                    <h6 className="card-subtitle mb-2 text-muted">{dateTime}</h6>
                    <p className="card-text">{weather.weather[0].description}</p>
                    <p className="card-text">Temperature: {weather.main.temp - 273.15}°C</p>
                    <p className="card-text">Humidity: {weather.main.humidity}%</p>
                    <p className="card-text">Wind Speed: {weather.wind.speed}km/h</p>
                </div>
            </div>
        );
    }

    return (
       < div className="mt-5">

           {
                showData === true ? (

                    <div className="container">
                        <div className="card mb-3 mx-auto bg-dark text-light">
                            <div className="row g-0"> 
                                < div className="col-md-4"> 
                                <h3 className="card-title">{weather.name}</h3>
                                <p className="card-date">{date}</p>
                                <p className="card-text">{weather.weather[0].description}</p>
                                <h1 className="card-temp">{Math.round(weather.main.temp - 273.15).toFixed(1)}°C</h1>


                                <img src= "https://images.pexels.com/photos/1666021/pexels-photo-1666021.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=2" className='img-fluid rounded-start' alt=".."/>
                            </div>
                             <div className="col-md-8"> 
                                 <div className="card-body text-start mt-2"> 

                                </div>

                                </div>
                             </div>
                        </div>
                    </div>
                ) : (
                     <h2 className="text-light"> sin datos </h2>
                )
           }


       </div>
    );

}

export default Card;
