/* eslint-disable jsx-a11y/heading-has-content */
import React from 'react';
import Spinner from './Spinner';


const Card = ({loadingData, showData, weather, forecast}) => {
/*
    var today = new Date();
    var date = today.getFullYear()+'-'+(today.getMonth()+1)+'-'+today.getDate();
    var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
    var dateTime = date+' '+time;
*/
    var today = new Date();
    var day = today.getDate();
    var month = today.getMonth() + 1;
    var year = today.getFullYear();
    var date = day + '/' + month + '/' + year;
    var url = "";
    var iconUrl = "";


    if(loadingData){
         return  <Spinner/>;
    }


    if(showData){
        url = "https://openweathermap.org/img/w/" ;
        iconUrl = url + weather.weather[0].icon + ".png";
    }

    return (
       < div className="mt-5">

           {
                showData === true ? (

                    <div className="container">
                        <div className="card mb-3 mx-auto bg-dark text-light">
                            <div className="row g-0"> 
                                < div className="col-md-4"> 
                                <h3 className="card-title">{weather.name}</h3>
                                <p className="card-date">{date}</p>
                                <p className="card-text">{weather.weather[0].description}</p>
                                <h1 className="card-temp">{(weather.main.temp - 273.15).toFixed(1)}Â°C</h1>
                                <p className="card-desc"> <img src={iconUrl} alt="icon"/>{weather.weather[0].description}</p>

                                <img src= "https://images.pexels.com/photos/2759806/pexels-photo-2759806.jpeg?cs=srgb&dl=pexels-rudolf-kirchner-2759806.jpg&fm=jpg"/>
                            </div>
                             <div className="col-md-8"> 
                                 <div className="card-body text-start mt-2"> 

                                </div>

                                </div>
                             </div>
                        </div>
                    </div>
                ) : (
                     <h2 className="text-light"> sin datos </h2>
                )
           }


       </div>
    );

}

export default Card;
